import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";

import { UserApi } from "../Api/UserApi";

export default function LoginScreen({ navigation }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [seePassword, setSeePassword] = useState(true);
  const [checkValidEmail, setCheckValidEmail] = useState(false);

  const handleCheckEmail = (text) => {
    let re = /\S+@\S+\.\S+/;
    let regex = /^[\+]?[(]?[0-9]{3}[)]?[-\s\.]?[0-9]{3}[-\s\.]?[0-9]{4,6}$/im;

    setEmail(text);
    setCheckValidEmail(!(re.test(text) || regex.test(text)));
  };

  const checkPasswordValidity = (value) => {
    if (!/^\S*$/.test(value)) return "Password must not contain Whitespaces.";
    if (!/^(?=.*[A-Z]).*$/.test(value)) return "Password must have at least one Uppercase Character.";
    if (!/^(?=.*[a-z]).*$/.test(value)) return "Password must have at least one Lowercase Character.";
    if (!/^(?=.*[0-9]).*$/.test(value)) return "Password must contain at least one Digit.";
    if (!/^.{8,}$/.test(value)) return "Password must be at least 8 Characters Long.";
    return null;
  };

  const handleLogin = async () => {
    const checkPassword = checkPasswordValidity(password);
    if (!checkPassword) {

      alert("Login Successful");
          navigation.replace("Profile");
      /*
      try {
        const result = await UserApi({
          username: email.toLowerCase(),
          password: password,
        });

        if (result && result.status === 200) {
          await AsyncStorage.setItem("access_token", result.data.access_token);
          alert("Login Successful");
          navigation.replace("Home");
        }
      } catch (error) {
        console.error(error);
        alert("Login failed");
      }
        */
    } else {
      alert(checkPassword);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.wrapperInput}>
        <TextInput
          style={styles.input}
          placeholder="Email"
          value={email}
          onChangeText={handleCheckEmail}
        />
      </View>
      {checkValidEmail && <Text style={styles.textFailed}>Wrong format email</Text>}
      
      <View style={styles.wrapperInput}>
        <TextInput
          style={styles.input}
          placeholder="Password"
          value={password}
          secureTextEntry={seePassword}
          onChangeText={setPassword}
        />
        <TouchableOpacity
          style={styles.wrapperIcon}
          onPress={() => setSeePassword(!seePassword)}
        >
          <Text>{seePassword ? "Show" : "Hide"}</Text>
        </TouchableOpacity>
      </View>
      
      {(email === "" || password === "" || checkValidEmail) ? (
        <TouchableOpacity disabled style={styles.buttonDisable} onPress={handleLogin}>
          <Text style={styles.text}>Login</Text>
        </TouchableOpacity>
      ) : (
        <TouchableOpacity style={styles.button} onPress={handleLogin}>
          <Text style={styles.text}>Login</Text>
        </TouchableOpacity>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    marginHorizontal: 20,
  },
  wrapperInput: {
    borderWidth: 0.5,
    borderRadius: 5,
    borderColor: "grey",
    marginTop: 10,
    flexDirection: "row",
    alignItems: "center",
  },
  input: {
    padding: 10,
    width: "100%",
  },
  wrapperIcon: {
    position: "absolute",
    right: 0,
    padding: 10,
  },
  button: {
    padding: 10,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "orange",
    borderRadius: 5,
    marginTop: 25,
  },
  buttonDisable: {
    padding: 10,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "grey",
    borderRadius: 5,
    marginTop: 25,
  },
  text: {
    color: "white",
    fontWeight: "700",
  },
  textFailed: {
    alignSelf: "flex-end",
    color: "red",
  },
});
