import Api from "./Api";

export const UserApi = async (email, password) => {
  try {
    const result = await Api("/login", {
      method: "OPTIONS",
      headers: {
        "content-type": "application/json",
      },
      data: {
        username: email,
        password: password,
      },
    });
    return result;
  } catch (error) {
    return error.response.data;
  }
};
