/*
import axios from "axios";

// Helper function to get the CSRF token from cookies
export function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
          const cookie = cookies[i].trim();
          if (cookie.substring(0, name.length + 1) === (name + '=')) {
              cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
              break;
          }
      }
  }
  return cookieValue;
}

const Api = axios.create({
  baseURL: "http://127.0.0.1:8000/events/",
  responseType: "json",
  withCredentials: true,  // Necessary for cookie handling across requests
  headers: {
    'X-CSRFToken': getCookie('csrftoken'),  // Pass CSRF token correctly in headers
    'Content-Type': 'application/json',  // Ensure JSON requests
    'Accept': 'application/json'
  }
});

export default Api;
*/
