import React, { useState } from "react";
import { View, TextInput, Button, Alert } from "react-native";
import axios from "axios";
import { useNavigation } from "@react-navigation/native";

const LoginScreen = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigation = useNavigation();

  const handleLogin = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/login", {
        username: email,
        password: password,
      });
      console.log(response);
      const { access_token } = response.data;

      // Handle successful login
      // You can store the access token in AsyncStorage or Redux state for further usage

      // Example: Show an alert with the access token
      Alert.alert("Login Successful", `Access Token: ${access_token}`);

      // Navigate to "Home" screen
      navigation.navigate("home");

      // Clear email and password fields after successful login
      setEmail("");
      setPassword("");
    } catch (error) {
      // Handle login error
      // Display an error message to the user
      Alert.alert("Login Failed", "Incorrect email or password");
    }
  };

  return (
    <View>
      <TextInput placeholder="Email" value={email} onChangeText={setEmail} />
      <TextInput
        placeholder="Password"
        secureTextEntry
        value={password}
        onChangeText={setPassword}
      />
      <Button title="Login" onPress={handleLogin} />
    </View>
  );
};

export default LoginScreen;
