import Api from "./Api";

export const UserApi = async (data) => {
  try {
    const result = await Api("/login", {
      method: "POST",
      headers: {
        "Content-Type": "multipart/form-data",
      },
      data: data,
    });
    const data = await result;
    return result;
  } catch (error) {
    return error.response.data;
  }
};

