import React from 'react';
import { View, Text, Image, TouchableOpacity, StyleSheet } from 'react-native';


const MessageBox = ({ contact }) => {
  return (
    <TouchableOpacity style={styles.messageBox} onPress={() => openMessage(contact)}>
      <Image source= {{ uri: contact.profilePicture }} style={styles.profilePicture} />
      <View style={styles.textContainer}>
        <Text style={styles.contactName}>{contact.name}</Text>
        <Text style={styles.messagePreview}>{contact.messagePreview}</Text>
      </View>
    </TouchableOpacity>
  );
};

const openMessage = (contact) => {
  // Logic to open the message window
  console.log(`Opening messages for ${contact.name}`);
};

const styles = StyleSheet.create({
  messageBox: {
    flexDirection: 'row',
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  profilePicture: {
    width: 50,
    height: 50,
    borderRadius: 25,
  },
  textContainer: {
    marginLeft: 10,
    justifyContent: 'center',
  },
  contactName: {
    fontWeight: 'bold',
  },
  messagePreview: {
    color: '#888',
  },
});

export default MessageBox;
